INSERT INTO `message` VALUES ('20', 'Cargotrans Logistics Co., Ltd', 'Sunny', '', 'sunny@cargotrans.vn', '', '', '', 'Dear Friends,\r\n\r\nSo long not to contact with you and hope everything is going well.\r\n\r\nOur VIP customer will change the trade term as FOB.\r\n\r\nBefore they arrange many CNF shipment from Shanghai\r\n\r\nSo if we can offer the competitive rates to them. They can consider to support us.\r\n\r\n1) 5*20GP from Shanghai to Haiphong port\r\n\r\n2) 5*20GP from Shanghai to Hochiminh port\r\n\r\nNeed 21days free time at destination.\r\n\r\nI have no target rates. Please advise your best rates to me. Because customer need to check the FOB cost, if the rates are competitive, they will change the trade terms as FOB.\r\n\r\nWaiting for your early respond\r\n\r\nMany thanks,', '2017-05-19 15:08:47');

-- ----------------------------
-- Table structure for `news`
-- ----------------------------
DROP TABLE IF EXISTS `news`;
CREATE TABLE `news` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `subject` varchar(200) NOT NULL DEFAULT '',
  `content` text,
  `time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `language` varchar(45) NOT NULL DEFAULT 'zh',
  PRIMARY KEY (`id`),
  KEY `time` (`time`),
  KEY `subject` (`subject`)
) ENGINE=InnoDB AUTO_INCREMENT=22 DEFAULT CHARSET=utf8;

-- ----------------------------
